import React, { Component } from 'react'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import { List, ListItem } from 'material-ui/List';
import RaisedButton from 'material-ui/RaisedButton';


class userConfirm extends Component {

  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = e => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { name, email, role, isUpdate, isCommunication } = this.props.values;

    return (


      <section className="mainSection">
        <section className="formSection">
          <h2>Please review your information and click submit to continue !</h2>
          <List>
            <ListItem
              primaryText='Name'
              secondaryText={name} />
            <ListItem
              primaryText="email"
              secondaryText={email} />
            <ListItem
              primaryText="role"
              secondaryText={role} />
            <ListItem
              primaryText="Updates from Tray.io ?"
              secondaryText={isUpdate ? 'Yes, i want to receive updates from Tray.io' : 'No, I do not wat to receive Updates'}
            />
            <ListItem
              primaryText="Do you want to receive communicational emails from Tray.io ?"
              secondaryText={isCommunication ? 'Yes, i want to receive communicational emais from Tray.io' : 'No, I do not want to receive communicational emails from Tray.io'}
            />

          </List>

          <button
            className="btn"
            onClick={this.back}
          >Back</button>

          <button
            className="btn"
            onClick={this.continue}
          >Continue & Confirm</button>
        </section>
      </section>


    )

  }
}

const styles = {
  button: {
    margin: 15
  }
}

export default userConfirm
